SELECT SUPPLIER.S_PHONE, PART.P_BRAND, PARTSUPP.PS_SUPPKEY, AVG(PARTSUPP.PS_SUPPKEY) FROM PART JOIN PARTSUPP ON PARTSUPP.PS_PARTKEY=PART.P_PARTKEY JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY WHERE PARTSUPP.PS_AVAILQTY > 8895 GROUP BY SUPPLIER.S_PHONE, PART.P_BRAND, PARTSUPP.PS_SUPPKEY ORDER BY PARTSUPP.PS_SUPPKEY ASC, SUPPLIER.S_PHONE DESC;
