SELECT CUSTOMER.C_CUSTKEY, NATION.N_NATIONKEY, SUPPLIER.S_PHONE, PARTSUPP.PS_SUPPLYCOST, AVG(NATION.N_REGIONKEY) FROM CUSTOMER JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY GROUP BY CUSTOMER.C_CUSTKEY, NATION.N_NATIONKEY, SUPPLIER.S_PHONE, PARTSUPP.PS_SUPPLYCOST;
