SELECT ORDERS.O_SHIPPRIORITY, NATION.N_REGIONKEY, CUSTOMER.C_ACCTBAL, MIN(CUSTOMER.C_NAME), AVG(ORDERS.O_SHIPPRIORITY) FROM NATION JOIN CUSTOMER ON CUSTOMER.C_NATIONKEY=NATION.N_NATIONKEY JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY GROUP BY ORDERS.O_SHIPPRIORITY, NATION.N_REGIONKEY, CUSTOMER.C_ACCTBAL ORDER BY ORDERS.O_SHIPPRIORITY DESC, NATION.N_REGIONKEY ASC, CUSTOMER.C_ACCTBAL ASC;
