SELECT NATION.N_NATIONKEY, LINEITEM.L_SHIPMODE, PARTSUPP.PS_SUPPLYCOST, REGION.R_REGIONKEY, SUPPLIER.S_SUPPKEY, MIN(NATION.N_NATIONKEY) FROM REGION JOIN NATION ON NATION.N_REGIONKEY=REGION.R_REGIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY GROUP BY NATION.N_NATIONKEY, LINEITEM.L_SHIPMODE, PARTSUPP.PS_SUPPLYCOST, REGION.R_REGIONKEY, SUPPLIER.S_SUPPKEY ORDER BY MIN(NATION.N_NATIONKEY) DESC;
