SELECT NATION.N_NAME, REGION.R_REGIONKEY, CUSTOMER.C_NATIONKEY, MIN(CUSTOMER.C_PHONE) FROM REGION JOIN NATION ON NATION.N_REGIONKEY=REGION.R_REGIONKEY JOIN CUSTOMER ON CUSTOMER.C_NATIONKEY=NATION.N_NATIONKEY GROUP BY NATION.N_NAME, REGION.R_REGIONKEY, CUSTOMER.C_NATIONKEY HAVING MIN(CUSTOMER.C_PHONE) != '30-165-277-3269' ORDER BY MIN(CUSTOMER.C_PHONE) DESC;
