SELECT PARTSUPP.PS_AVAILQTY, LINEITEM.L_SUPPKEY, ORDERS.O_ORDERPRIORITY, PART.P_CONTAINER FROM PART JOIN PARTSUPP ON PARTSUPP.PS_PARTKEY=PART.P_PARTKEY JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY WHERE LINEITEM.L_SHIPMODE < 'REG AIR   ';
