SELECT LINEITEM.L_ORDERKEY, SUPPLIER.S_ADDRESS, PARTSUPP.PS_AVAILQTY FROM LINEITEM, PARTSUPP, SUPPLIER WHERE PARTSUPP.PS_COMMENT < ' FINAL PACKAGES WAKE QUICKLY ACROSS THE BLITHELY IRONIC INSTRUCTIONS. REGULAR PAINS INTEGRATE SLYLY ACROSS THE DEPOSITS. CAREFULLY REGULAR PINTO BEANS AMONG THE CLOSE' AND SUPPLIER.S_NAME > 'SUPPLIER#000000004       ' ORDER BY PARTSUPP.PS_AVAILQTY DESC;
