SELECT PARTSUPP.PS_AVAILQTY, LINEITEM.L_COMMENT, MAX(PARTSUPP.PS_COMMENT), COUNT(LINEITEM.L_COMMITDATE) FROM PARTSUPP, LINEITEM WHERE PARTSUPP.PS_PARTKEY != 189 AND LINEITEM.L_DISCOUNT = 0.00 GROUP BY PARTSUPP.PS_AVAILQTY, LINEITEM.L_COMMENT HAVING MAX(PARTSUPP.PS_COMMENT) <= 'REFULLY ALONGSIDE OF THE QUICKLY BOLD EXCUSES. ENTICING, BOLD ';
