SELECT ORDERS.O_ORDERSTATUS, PARTSUPP.PS_AVAILQTY, SUPPLIER.S_PHONE, CUSTOMER.C_NAME, NATION.N_COMMENT, MIN(SUPPLIER.S_ADDRESS) FROM ORDERS JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY GROUP BY ORDERS.O_ORDERSTATUS, PARTSUPP.PS_AVAILQTY, SUPPLIER.S_PHONE, CUSTOMER.C_NAME, NATION.N_COMMENT ORDER BY MIN(SUPPLIER.S_ADDRESS) ASC;
