SELECT NATION.N_NAME, PARTSUPP.PS_AVAILQTY, LINEITEM.L_SUPPKEY, SUPPLIER.S_PHONE FROM NATION JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY ORDER BY PARTSUPP.PS_AVAILQTY ASC, NATION.N_NAME DESC, SUPPLIER.S_PHONE ASC;
