SELECT PARTSUPP.PS_COMMENT, NATION.N_REGIONKEY, SUPPLIER.S_NAME, CUSTOMER.C_NAME FROM PARTSUPP JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN NATION ON NATION.N_NATIONKEY=SUPPLIER.S_NATIONKEY JOIN CUSTOMER ON CUSTOMER.C_NATIONKEY=NATION.N_NATIONKEY WHERE CUSTOMER.C_ADDRESS <= 'TLGALGDXWBMMV,6AGLYWYDYIZ9MKZCY8GL,W6T1B';
