SELECT NATION.N_NAME, CUSTOMER.C_ACCTBAL, REGION.R_NAME, COUNT(REGION.R_COMMENT), MIN(CUSTOMER.C_MKTSEGMENT) FROM CUSTOMER JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN REGION ON REGION.R_REGIONKEY=NATION.N_REGIONKEY GROUP BY NATION.N_NAME, CUSTOMER.C_ACCTBAL, REGION.R_NAME HAVING MIN(CUSTOMER.C_MKTSEGMENT) = 'AUTOMOBILE' AND COUNT(REGION.R_COMMENT) = 8 ORDER BY MIN(CUSTOMER.C_MKTSEGMENT) DESC, COUNT(REGION.R_COMMENT) ASC;
