SELECT SUPPLIER.S_NAME, PART.P_SIZE, PARTSUPP.PS_AVAILQTY, MIN(PART.P_CONTAINER) FROM SUPPLIER JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY JOIN PART ON PART.P_PARTKEY=PARTSUPP.PS_PARTKEY WHERE SUPPLIER.S_NAME <= 'SUPPLIER#000000003       ' GROUP BY SUPPLIER.S_NAME, PART.P_SIZE, PARTSUPP.PS_AVAILQTY;
