SELECT PART.P_BRAND, SUPPLIER.S_ACCTBAL, PARTSUPP.PS_COMMENT, MAX(PARTSUPP.PS_AVAILQTY), COUNT(SUPPLIER.S_SUPPKEY) FROM SUPPLIER JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY JOIN PART ON PART.P_PARTKEY=PARTSUPP.PS_PARTKEY WHERE PART.P_PARTKEY < 56 GROUP BY PART.P_BRAND, SUPPLIER.S_ACCTBAL, PARTSUPP.PS_COMMENT ORDER BY MAX(PARTSUPP.PS_AVAILQTY) ASC;
