SELECT SUPPLIER.S_SUPPKEY, PARTSUPP.PS_SUPPKEY, MIN(SUPPLIER.S_NAME), AVG(PARTSUPP.PS_AVAILQTY) FROM SUPPLIER JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY GROUP BY SUPPLIER.S_SUPPKEY, PARTSUPP.PS_SUPPKEY HAVING AVG(PARTSUPP.PS_AVAILQTY) != 5334 AND MIN(SUPPLIER.S_NAME) <= 'SUPPLIER#000000001       ';
