SELECT PARTSUPP.PS_AVAILQTY, SUPPLIER.S_NATIONKEY, MIN(SUPPLIER.S_ADDRESS), MIN(SUPPLIER.S_COMMENT), MAX(PARTSUPP.PS_PARTKEY) FROM SUPPLIER, PARTSUPP WHERE SUPPLIER.S_ACCTBAL < 5302.37 AND PARTSUPP.PS_AVAILQTY != 2721 GROUP BY PARTSUPP.PS_AVAILQTY, SUPPLIER.S_NATIONKEY HAVING MAX(PARTSUPP.PS_PARTKEY) <= 200;
