SELECT NATION.N_NAME, PARTSUPP.PS_PARTKEY, LINEITEM.L_LINESTATUS, CUSTOMER.C_NAME, SUPPLIER.S_NATIONKEY FROM CUSTOMER JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY;
