SELECT CUSTOMER.C_ADDRESS, NATION.N_REGIONKEY, REGION.R_REGIONKEY, COUNT(REGION.R_NAME) FROM CUSTOMER JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN REGION ON REGION.R_REGIONKEY=NATION.N_REGIONKEY GROUP BY CUSTOMER.C_ADDRESS, NATION.N_REGIONKEY, REGION.R_REGIONKEY HAVING COUNT(REGION.R_NAME) < 2;
