SELECT ORDERS.O_ORDERPRIORITY, LINEITEM.L_RECEIPTDATE, SUPPLIER.S_PHONE, PARTSUPP.PS_SUPPKEY, COUNT(LINEITEM.L_RECEIPTDATE) FROM ORDERS JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY GROUP BY ORDERS.O_ORDERPRIORITY, LINEITEM.L_RECEIPTDATE, SUPPLIER.S_PHONE, PARTSUPP.PS_SUPPKEY;
