SELECT SUPPLIER.S_PHONE, CUSTOMER.C_ADDRESS, NATION.N_COMMENT, PARTSUPP.PS_COMMENT FROM PARTSUPP JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN NATION ON NATION.N_NATIONKEY=SUPPLIER.S_NATIONKEY JOIN CUSTOMER ON CUSTOMER.C_NATIONKEY=NATION.N_NATIONKEY WHERE CUSTOMER.C_MKTSEGMENT <= 'BUILDING  ' ORDER BY SUPPLIER.S_PHONE ASC;
