SELECT PART.P_SIZE, SUPPLIER.S_SUPPKEY, NATION.N_NAME, PARTSUPP.PS_PARTKEY FROM NATION JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY JOIN PART ON PART.P_PARTKEY=PARTSUPP.PS_PARTKEY ORDER BY SUPPLIER.S_SUPPKEY ASC, NATION.N_NAME ASC;
