SELECT PARTSUPP.PS_SUPPKEY, CUSTOMER.C_NAME, SUPPLIER.S_ADDRESS, LINEITEM.L_LINESTATUS, ORDERS.O_ORDERPRIORITY, NATION.N_NATIONKEY FROM ORDERS, CUSTOMER, NATION, SUPPLIER, PARTSUPP, LINEITEM ORDER BY ORDERS.O_ORDERPRIORITY ASC, LINEITEM.L_LINESTATUS ASC, CUSTOMER.C_NAME DESC, SUPPLIER.S_ADDRESS DESC;
