SELECT LINEITEM.L_SHIPMODE, ORDERS.O_TOTALPRICE, PARTSUPP.PS_SUPPLYCOST, CUSTOMER.C_COMMENT FROM PARTSUPP JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY WHERE CUSTOMER.C_ACCTBAL < -551.37 AND ORDERS.O_COMMENT > ' THE PENDING PACKAGES WAKE AFTER THE QUICKLY SPECI';
