SELECT LINEITEM.L_COMMITDATE, ORDERS.O_SHIPPRIORITY, PART.P_NAME, PARTSUPP.PS_AVAILQTY, COUNT(PART.P_CONTAINER) FROM ORDERS JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY JOIN PART ON PART.P_PARTKEY=PARTSUPP.PS_PARTKEY GROUP BY LINEITEM.L_COMMITDATE, ORDERS.O_SHIPPRIORITY, PART.P_NAME, PARTSUPP.PS_AVAILQTY ORDER BY COUNT(PART.P_CONTAINER) DESC;
