SELECT PARTSUPP.PS_COMMENT, CUSTOMER.C_MKTSEGMENT, SUPPLIER.S_ACCTBAL, LINEITEM.L_SHIPMODE, NATION.N_NATIONKEY, ORDERS.O_CLERK FROM ORDERS JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN NATION ON NATION.N_NATIONKEY=SUPPLIER.S_NATIONKEY JOIN CUSTOMER ON CUSTOMER.C_NATIONKEY=NATION.N_NATIONKEY WHERE ORDERS.O_SHIPPRIORITY >= 0 AND LINEITEM.L_COMMITDATE > '1996-07-27' ORDER BY LINEITEM.L_SHIPMODE ASC, CUSTOMER.C_MKTSEGMENT ASC, SUPPLIER.S_ACCTBAL ASC;
