SELECT SUPPLIER.S_COMMENT, NATION.N_COMMENT, PARTSUPP.PS_COMMENT, PART.P_SIZE FROM PART JOIN PARTSUPP ON PARTSUPP.PS_PARTKEY=PART.P_PARTKEY JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN NATION ON NATION.N_NATIONKEY=SUPPLIER.S_NATIONKEY ORDER BY NATION.N_COMMENT ASC, PARTSUPP.PS_COMMENT DESC, SUPPLIER.S_COMMENT ASC;
