SELECT PART.P_RETAILPRICE, PARTSUPP.PS_AVAILQTY, NATION.N_REGIONKEY, CUSTOMER.C_ADDRESS, SUPPLIER.S_ACCTBAL FROM CUSTOMER JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=SUPPLIER.S_SUPPKEY JOIN PART ON PART.P_PARTKEY=PARTSUPP.PS_PARTKEY ORDER BY SUPPLIER.S_ACCTBAL ASC, NATION.N_REGIONKEY DESC, PARTSUPP.PS_AVAILQTY ASC;
