SELECT SUPPLIER.S_PHONE, PART.P_TYPE, PARTSUPP.PS_SUPPLYCOST, REGION.R_NAME, NATION.N_COMMENT, MIN(PARTSUPP.PS_SUPPLYCOST) FROM REGION, NATION, SUPPLIER, PARTSUPP, PART GROUP BY SUPPLIER.S_PHONE, PART.P_TYPE, PARTSUPP.PS_SUPPLYCOST, REGION.R_NAME, NATION.N_COMMENT HAVING MIN(PARTSUPP.PS_SUPPLYCOST) < 510.23 ORDER BY SUPPLIER.S_PHONE ASC, REGION.R_NAME ASC;
