SELECT REGION.R_NAME, SUPPLIER.S_ACCTBAL, NATION.N_REGIONKEY FROM SUPPLIER JOIN NATION ON NATION.N_NATIONKEY=SUPPLIER.S_NATIONKEY JOIN REGION ON REGION.R_REGIONKEY=NATION.N_REGIONKEY WHERE SUPPLIER.S_COMMENT >= 'EACH SLYLY ABOVE THE CAREFUL' AND NATION.N_NATIONKEY = 3827 AND SUPPLIER.S_COMMENT != 'FINAL ACCOUNTS. REGULAR DOLPHINS USE AGAINST THE FURIOUSLY IRONIC DECOYS. ';
