SELECT PARTSUPP.PS_SUPPLYCOST, SUPPLIER.S_COMMENT, NATION.N_NATIONKEY, SUM(PARTSUPP.PS_PARTKEY) FROM PARTSUPP JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN NATION ON NATION.N_NATIONKEY=SUPPLIER.S_NATIONKEY GROUP BY PARTSUPP.PS_SUPPLYCOST, SUPPLIER.S_COMMENT, NATION.N_NATIONKEY HAVING SUM(PARTSUPP.PS_PARTKEY) < 46;
