SELECT SUPPLIER.S_NAME, PARTSUPP.PS_PARTKEY, NATION.N_COMMENT, REGION.R_REGIONKEY, COUNT(SUPPLIER.S_PHONE) FROM PARTSUPP JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN NATION ON NATION.N_NATIONKEY=SUPPLIER.S_NATIONKEY JOIN REGION ON REGION.R_REGIONKEY=NATION.N_REGIONKEY WHERE PARTSUPP.PS_COMMENT != 'L ACCOUNTS ARE QUICKLY AFTER THE UNUSUAL PACKAGES. REGULAR ACCOUNTS WAKE AMONG THE QUICKLY EVEN ACCOUNTS. EVEN, IRONIC' GROUP BY SUPPLIER.S_NAME, PARTSUPP.PS_PARTKEY, NATION.N_COMMENT, REGION.R_REGIONKEY ORDER BY COUNT(SUPPLIER.S_PHONE) DESC;
