SELECT NATION.N_REGIONKEY, LINEITEM.L_COMMENT, PARTSUPP.PS_SUPPLYCOST, ORDERS.O_ORDERPRIORITY, CUSTOMER.C_NAME, SUPPLIER.S_NAME, MIN(SUPPLIER.S_PHONE) FROM ORDERS, LINEITEM, PARTSUPP, SUPPLIER, NATION, CUSTOMER GROUP BY NATION.N_REGIONKEY, LINEITEM.L_COMMENT, PARTSUPP.PS_SUPPLYCOST, ORDERS.O_ORDERPRIORITY, CUSTOMER.C_NAME, SUPPLIER.S_NAME ORDER BY MIN(SUPPLIER.S_PHONE) ASC;
