SELECT CUSTOMER.C_NATIONKEY, SUPPLIER.S_ACCTBAL, ORDERS.O_ORDERKEY, NATION.N_REGIONKEY, MIN(ORDERS.O_ORDERDATE) FROM SUPPLIER, NATION, CUSTOMER, ORDERS GROUP BY CUSTOMER.C_NATIONKEY, SUPPLIER.S_ACCTBAL, ORDERS.O_ORDERKEY, NATION.N_REGIONKEY HAVING MIN(ORDERS.O_ORDERDATE) = '1992-03-28' ORDER BY ORDERS.O_ORDERKEY DESC, SUPPLIER.S_ACCTBAL ASC, NATION.N_REGIONKEY DESC, CUSTOMER.C_NATIONKEY DESC;
