SELECT LINEITEM.L_SHIPDATE, CUSTOMER.C_ADDRESS, ORDERS.O_CUSTKEY, NATION.N_NATIONKEY, REGION.R_COMMENT, MIN(REGION.R_COMMENT) FROM LINEITEM, ORDERS, CUSTOMER, NATION, REGION WHERE ORDERS.O_ORDERDATE > '1996-04-30' GROUP BY LINEITEM.L_SHIPDATE, CUSTOMER.C_ADDRESS, ORDERS.O_CUSTKEY, NATION.N_NATIONKEY, REGION.R_COMMENT ORDER BY MIN(REGION.R_COMMENT) DESC;
