SELECT ORDERS.O_ORDERDATE, SUPPLIER.S_COMMENT, NATION.N_NATIONKEY, PARTSUPP.PS_SUPPKEY, CUSTOMER.C_NATIONKEY, COUNT(ORDERS.O_ORDERPRIORITY), MAX(CUSTOMER.C_ACCTBAL), MAX(SUPPLIER.S_COMMENT), MIN(NATION.N_NAME), MAX(NATION.N_NAME) FROM PARTSUPP JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN NATION ON NATION.N_NATIONKEY=SUPPLIER.S_NATIONKEY JOIN CUSTOMER ON CUSTOMER.C_NATIONKEY=NATION.N_NATIONKEY JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY GROUP BY ORDERS.O_ORDERDATE, SUPPLIER.S_COMMENT, NATION.N_NATIONKEY, PARTSUPP.PS_SUPPKEY, CUSTOMER.C_NATIONKEY;
