SELECT ORDERS.O_ORDERDATE, SUPPLIER.S_NAME, LINEITEM.L_TAX, CUSTOMER.C_MKTSEGMENT, PARTSUPP.PS_SUPPLYCOST, SUM(PARTSUPP.PS_SUPPLYCOST) FROM CUSTOMER JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY GROUP BY ORDERS.O_ORDERDATE, SUPPLIER.S_NAME, LINEITEM.L_TAX, CUSTOMER.C_MKTSEGMENT, PARTSUPP.PS_SUPPLYCOST;
