SELECT SUPPLIER.S_ACCTBAL, NATION.N_NATIONKEY, PARTSUPP.PS_SUPPKEY, REGION.R_NAME, MAX(PARTSUPP.PS_AVAILQTY) FROM PARTSUPP JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN NATION ON NATION.N_NATIONKEY=SUPPLIER.S_NATIONKEY JOIN REGION ON REGION.R_REGIONKEY=NATION.N_REGIONKEY GROUP BY SUPPLIER.S_ACCTBAL, NATION.N_NATIONKEY, PARTSUPP.PS_SUPPKEY, REGION.R_NAME HAVING MAX(PARTSUPP.PS_AVAILQTY) >= 319;
