SELECT LINEITEM.L_RETURNFLAG, PARTSUPP.PS_AVAILQTY, PART.P_COMMENT, AVG(LINEITEM.L_SUPPKEY) FROM LINEITEM JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY JOIN PART ON PART.P_PARTKEY=PARTSUPP.PS_PARTKEY WHERE LINEITEM.L_LINENUMBER > 3 GROUP BY LINEITEM.L_RETURNFLAG, PARTSUPP.PS_AVAILQTY, PART.P_COMMENT ORDER BY LINEITEM.L_RETURNFLAG DESC;
