SELECT LINEITEM.L_SHIPMODE, SUPPLIER.S_NAME, NATION.N_COMMENT, PARTSUPP.PS_SUPPKEY FROM LINEITEM JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN NATION ON NATION.N_NATIONKEY=SUPPLIER.S_NATIONKEY WHERE NATION.N_NATIONKEY < NIC ACCOUNTS. FINAL ACCOUNTS SLEEP FURIOUSLY ABOUT THE IRONIC, BOLD PACKAGES. REGULAR, REGULAR ACCOUNTS AND PARTSUPP.PS_SUPPKEY = 98 ORDER BY PARTSUPP.PS_SUPPKEY DESC, SUPPLIER.S_NAME ASC, LINEITEM.L_SHIPMODE ASC;
