SELECT LINEITEM.L_DISCOUNT, PART.P_BRAND, PARTSUPP.PS_AVAILQTY, COUNT(LINEITEM.L_TAX) FROM LINEITEM JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY JOIN PART ON PART.P_PARTKEY=PARTSUPP.PS_PARTKEY WHERE LINEITEM.L_QUANTITY != 43 GROUP BY LINEITEM.L_DISCOUNT, PART.P_BRAND, PARTSUPP.PS_AVAILQTY ORDER BY COUNT(LINEITEM.L_TAX) DESC;
