SELECT CUSTOMER.C_ACCTBAL, LINEITEM.L_PARTKEY, ORDERS.O_ORDERSTATUS, PARTSUPP.PS_COMMENT, SUPPLIER.S_ACCTBAL, MIN(SUPPLIER.S_PHONE), MIN(ORDERS.O_CLERK) FROM CUSTOMER JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY JOIN SUPPLIER ON SUPPLIER.S_SUPPKEY=PARTSUPP.PS_SUPPKEY GROUP BY CUSTOMER.C_ACCTBAL, LINEITEM.L_PARTKEY, ORDERS.O_ORDERSTATUS, PARTSUPP.PS_COMMENT, SUPPLIER.S_ACCTBAL;
