SELECT CUSTOMER.C_CUSTKEY, REGION.R_COMMENT, ORDERS.O_ORDERPRIORITY, NATION.N_NATIONKEY FROM REGION JOIN NATION ON NATION.N_REGIONKEY=REGION.R_REGIONKEY JOIN CUSTOMER ON CUSTOMER.C_NATIONKEY=NATION.N_NATIONKEY JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY WHERE NATION.N_COMMENT < '810.68' AND NATION.N_REGIONKEY <= Y PENDING FOXES NAG BLITHELY ;
