SELECT LINEITEM.L_TAX, ORDERS.O_ORDERPRIORITY, CUSTOMER.C_COMMENT, MAX(ORDERS.O_COMMENT), AVG(CUSTOMER.C_NATIONKEY) FROM CUSTOMER JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY WHERE CUSTOMER.C_ADDRESS <= '7 QOQGQQSY9JFV51BC71JCHJSD0' GROUP BY LINEITEM.L_TAX, ORDERS.O_ORDERPRIORITY, CUSTOMER.C_COMMENT ORDER BY AVG(CUSTOMER.C_NATIONKEY) ASC;
