SELECT ORDERS.O_SHIPPRIORITY, LINEITEM.L_EXTENDEDPRICE FROM LINEITEM, ORDERS WHERE LINEITEM.L_RECEIPTDATE <= '1998-08-15' AND LINEITEM.L_COMMITDATE = '1995-05-11' AND LINEITEM.L_COMMENT != 'BOVE THE EVEN FOXES. UNUSUAL THEODOLI' AND ORDERS.O_SHIPPRIORITY <= 0 AND ORDERS.O_ORDERSTATUS != 'P' ORDER BY LINEITEM.L_EXTENDEDPRICE ASC, ORDERS.O_SHIPPRIORITY ASC;
