SELECT NATION.N_NATIONKEY, REGION.R_REGIONKEY, MIN(NATION.N_REGIONKEY) FROM REGION JOIN NATION ON NATION.N_REGIONKEY=REGION.R_REGIONKEY WHERE REGION.R_COMMENT > 'SS PACKAGES HAGGLE FURIOUSLY EXPRESS, REGULAR DEPOSITS. EVEN, E' GROUP BY NATION.N_NATIONKEY, REGION.R_REGIONKEY ORDER BY MIN(NATION.N_REGIONKEY) ASC;
