SELECT LINEITEM.L_EXTENDEDPRICE, CUSTOMER.C_PHONE, NATION.N_NATIONKEY, ORDERS.O_CLERK, REGION.R_NAME FROM REGION JOIN NATION ON NATION.N_REGIONKEY=REGION.R_REGIONKEY JOIN CUSTOMER ON CUSTOMER.C_NATIONKEY=NATION.N_NATIONKEY JOIN ORDERS ON ORDERS.O_CUSTKEY=CUSTOMER.C_CUSTKEY JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY ORDER BY CUSTOMER.C_PHONE DESC, REGION.R_NAME ASC, NATION.N_NATIONKEY DESC;
