SELECT SUPPLIER.S_NAME, NATION.N_NATIONKEY, CUSTOMER.C_NAME, MIN(CUSTOMER.C_ACCTBAL) FROM CUSTOMER JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY WHERE CUSTOMER.C_PHONE = '16-155-215-1315' GROUP BY SUPPLIER.S_NAME, NATION.N_NATIONKEY, CUSTOMER.C_NAME ORDER BY SUPPLIER.S_NAME ASC, NATION.N_NATIONKEY ASC, CUSTOMER.C_NAME ASC;
