SELECT LINEITEM.L_EXTENDEDPRICE, PARTSUPP.PS_COMMENT, PART.P_SIZE, ORDERS.O_CUSTKEY FROM ORDERS JOIN LINEITEM ON LINEITEM.L_ORDERKEY=ORDERS.O_ORDERKEY JOIN PARTSUPP ON PARTSUPP.PS_SUPPKEY=LINEITEM.L_SUPPKEY JOIN PART ON PART.P_PARTKEY=PARTSUPP.PS_PARTKEY WHERE ORDERS.O_ORDERSTATUS < 'P' ORDER BY ORDERS.O_CUSTKEY ASC, PART.P_SIZE DESC, LINEITEM.L_EXTENDEDPRICE DESC, PARTSUPP.PS_COMMENT ASC;
