SELECT ORDERS.O_ORDERKEY, LINEITEM.L_LINESTATUS, PARTSUPP.PS_SUPPLYCOST, COUNT(LINEITEM.L_COMMITDATE) FROM PARTSUPP JOIN LINEITEM ON LINEITEM.L_SUPPKEY=PARTSUPP.PS_SUPPKEY JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY GROUP BY ORDERS.O_ORDERKEY, LINEITEM.L_LINESTATUS, PARTSUPP.PS_SUPPLYCOST HAVING COUNT(LINEITEM.L_COMMITDATE) <= 8;
