SELECT ORDERS.O_ORDERPRIORITY, LINEITEM.L_COMMENT, NATION.N_REGIONKEY, CUSTOMER.C_MKTSEGMENT, SUPPLIER.S_SUPPKEY FROM LINEITEM JOIN ORDERS ON ORDERS.O_ORDERKEY=LINEITEM.L_ORDERKEY JOIN CUSTOMER ON CUSTOMER.C_CUSTKEY=ORDERS.O_CUSTKEY JOIN NATION ON NATION.N_NATIONKEY=CUSTOMER.C_NATIONKEY JOIN SUPPLIER ON SUPPLIER.S_NATIONKEY=NATION.N_NATIONKEY;
